parameters.jsCode:
// Get the incoming data
const body = $input.item.json.body || $input.item.json;

// Check if this is a new conversation (only email) or continuation (has message)
const isNewConversation = !body.message && body.userEmail;
const isContinuation = body.message && body.userEmail && body.conversation;

if (isNewConversation) {
    // Handle new conversation
    return [{
        json: {
            userEmail: body.userEmail,
            conversation: [],
            questionCount: 0,
            isComplete: false,
            userId: `user_${Date.now()}`,
            isFirstExchange: true
        }
    }];
} else if (isContinuation) {
    // Handle conversation continuation
    return [{
        json: {
            userEmail: body.userEmail,
            userMessage: body.message,
            conversation: body.conversation || [],
            questionCount: parseInt(body.questionCount) || 0,
            isComplete: body.isComplete || false,
            userId: body.userId || `user_${Date.now()}`,
            isFirstExchange: false
        }
    }];
} else {
    // Error case
    return [{
        json: {
            error: "Invalid request format",
            userEmail: body.userEmail || "unknown",
            conversation: [],
            questionCount: 0,
            isComplete: false
        }
    }];
}

try {
    // Safety checks
    if (!$node || !$node["Pass Data Through"] || !$node["Pass Data Through"].json) {
        return [{
            json: {
                error: "No Gemini response received",
                userEmail: "error@fallback.com",
                conversation: [],
                isComplete: false,
                questionCount: 0
            }
        }];
    }

    // Get data from the correct nodes
    const geminiResponse = $node["Pass Data Through"].json;
    const initData = $node["Initialize Conversation"].json;

    // Safely extract AI response
    const aiText = geminiResponse?.candidates?.[0]?.content?.parts?.[0]?.text || "I couldn't generate a response. Please try again.";
    const cleanResponse = aiText.trim();

    // Initialize conversation array
    let currentConversation = [];
    if (Array.isArray(initData.conversation)) {
        currentConversation = [...initData.conversation];
    } else {
        currentConversation = [];
    }

    // Add user message FIRST (if not first exchange)
    const userMessage = initData.userMessage || "";
    const isFirstExchange = !userMessage || initData.isFirstExchange;

    if (!isFirstExchange && userMessage.trim()) {
        currentConversation.push({
            role: "user",
            content: userMessage.replace(/[^\w\s\.,!?-]/g, '') // âš ï¸ SANITIZE special characters
        });
    }

    // Then add AI response
    currentConversation.push({ 
        role: "assistant", 
        content: cleanResponse.replace(/[^\w\s\.,!?-]/g, '') // âš ï¸ SANITIZE special characters
    });

    // Rest of your existing logic...
    const currentQuestionCount = Number(initData.questionCount) || 0;
    const newQuestionCount = currentQuestionCount + 1;

    const userRequestedEnd = userMessage.toLowerCase().includes("end") || 
                            userMessage.toLowerCase().includes("close") ||
                            userMessage.toLowerCase().includes("done") ||
                            userMessage.toLowerCase().includes("finish");

    const hasEnoughQuestions = newQuestionCount >= 2;
    const isComplete = hasEnoughQuestions && userRequestedEnd;

    return [{
        json: {
            status: isComplete ? "consultation_complete" : "conversation_active",
            response: cleanResponse,
            isComplete: isComplete,
            conversation: currentConversation,
            questionCount: newQuestionCount,
            userEmail: initData.userEmail || '',
            userId: initData.userId || 'anonymous',
            nextAction: isComplete ? "check_email_for_report" : "continue_chat"
        }
    }];

} catch (error) {
    return [{
        json: {
            error: "Processing failed: " + error.message,
            status: "error",
            isComplete: false,
            conversation: [],
            questionCount: 0,
            userEmail: "error@fallback.com",
            fallback: true
        }
    }];
}

